mapper.update:
update blog


			title = #{title},


			update_time = #{updateTime},


			content = #{content},


			tag_name = #{tagName}


where uid = #{uid}

update blog
	set 


		title = #{value}


		create_time = #{value}


		update_time = #{value}


		content = #{value}


		tag_name = #{value}


where uid = #{uid}

update.foreach:
	update blog


			title = #{item.title},


			update_time = #{item.updateTime},


			content = #{item.content},


			tag_name = #{item.tagName}


     	where uid = #{item.uid}

mapper.select:
select
	count(id)
	from blog


		and id = #{id}


		and title = #{title}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and content = #{content}


		and tag_name = #{tagName}

select

	from blog


		and id = #{entity.id}


		and title = #{entity.title}


		and create_time = #{entity.createTime}


		and update_time = #{entity.updateTime}


		and content = #{entity.content}


		and tag_name = #{entity.tagName}
